"use strict";(self["webpackChunkvue_labour"]=self["webpackChunkvue_labour"]||[]).push([[228],{7228:function(t,e,n){n.r(e),n.d(e,{default:function(){return c}});var r=function(){var t=this,e=t._self._c;return e("div",[e("div",{staticStyle:{width:"65%",margin:"0 auto"}},[e("h1",[t._v("广西外国语学院二十周年校庆公告（第一号）")]),e("P",[t._v("青山东麓启巍峨，邕水北岸谱新篇。2024年广西外国语学院喜迎二十华诞，值此佳期，我们谨向长期以来关心支持学校改革发展的各级领导和各界友人，向海内外校友、全体退休教职工和全校师生员工致以衷心的感谢和崇高的敬意！ ")]),e("img",{attrs:{src:n(4616),alt:""}}),e("p",[t._v("薪传五合，开新朗耀；敢为人先，砥砺前行。广西外国语学院前身是创办于2004年6月的广西东方外语职业学院，2011年4月升格为全日制普通本科高校，是广西首家升本的民办普通本科高校，是广西唯一独立建制的外语类普通本科高校。2018年学校顺利通过教育部普通高等学校本科教学工作合格评估，2021年获批立项建设新增硕士学位授予单位，学校人才培养质量和办学水平实现了新跨越。学校现有南宁市五合校区和空港校区两个校区，校园总规划占地面积2500亩，设有16个院系，开设有60个本、专科专业（含方向），其中语言类专业13个，全日制在校生33000多人。 ")]),e("p",[t._v("誉享八桂，弦歌不辍；春华秋实，桃李芬芳。学校秉持“留住中国魂做好国际人”的校训，以“办人民满意的大学”为宗旨，弘扬“敢为人先砥砺奋进”大学精神，形成了“大爱致远”的良好校风。学校矢志不移坚持党的领导，始终坚持社会主义办学方向，落实立德树人根本任务，强化内涵发展，培养强外语、厚人文、具有家国情怀和国际视野的应用型人才，先后为社会输送了7万余名优秀人才，派送5000多名学生赴国外合作高校接受学分互认的课程学习和社会实践，办学以来共招收1500多名国际生，学校先后获全国民办教育先进集体、全区高校思想政治工作先进单位、全区两新组织党建工作示范点、广西壮族自治区文明校园、广西五四红旗团委等荣誉称号。 ")]),e("img",{attrs:{src:n(5040),alt:""}}),e("p",[t._v("不忘初心，牢记使命；承前启后，继往开来。广西外国语学院20周年校庆是学校发展史上一个重要里程碑。我们将在2024年11月15-17日举行以“风华正茂20载接续奋斗向未来——为建设特色鲜明的高水平民办高校团结奋斗”为主题的系列庆典活动，以期弘扬广西外院精神、凝聚校友力量、共创卓越未来。我们诚挚邀请各界宾朋拨冗莅临，共襄盛事；热切期盼广大校友重返母校，相聚五棵松下、凤鸣坡旁，重拾青春梦想、共谱未来华章。 ")])],1)])},u=[],i={},a=i,l=n(1656),s=(0,l.A)(a,r,u,!1,null,null,null),c=s.exports},4616:function(t,e,n){t.exports=n.p+"img/celebrating.93eb67bd.jpg"},5040:function(t,e,n){t.exports=n.p+"img/celebrating2.256e24a8.jpg"}}]);
